svg.script:

var chiudiFinestra = document.getElementById("chiudiFinestra"); 
var svgChiudi = document.getElementById("svgChiudi"); 
svgChiudi.addEventListener("mouseover", function(){ 
	chiudiFinestra.setAttribute('stroke', '#00ff00');
},false);

svgChiudi.addEventListener("mouseout", function(){ 
	chiudiFinestra.setAttribute('stroke', '#ff0000');
},false);
